var codes=[];

function toPercent(point){
    var str=Number(point*100).toFixed(2);
    str+="%";
    return str;
}

str.split(' ').forEach(function(e,i){
    // for(var i=0; i<meta_code.length; i++){
    //     if(e==meta_code[i].name){
    //         codes.push(meta_code[i].code);
    //     }
    // }
    meta_code.forEach(function (item, index) {
        if(e==item.name){
            codes.push(item.code);
        }
    })
});

var url='http://api.money.126.net/data/feed/'+codes.join(',')+',money.api';
// var url='http://api.money.126.net/data/feed/0600519,1000858,money.api';
function onRequest(req, res) {

    res.writeHead(200, {'Content-Type': 'text/html;charset=utf-8'});
    http.get(url, function (res) {
        var htmlData=[];
        res.on('data', function (data) {
            htmlData.push(data);
            htmlDataLength += data.length;
        });

        res.on('end', function () {
            var bufferHtmlData = Buffer.concat(htmlData, htmlDataLength);
            var charset = '';
            var decodeHtmlData;
            var htmlHeadCharset = '';
            var htmlHeadContent = '';
            var index = 0;

            var $ = cheerio.load(bufferHtmlData, {decodeEntities: false});

            $('meta', 'head').each(function (i, e) {

                htmlHeadCharset = $(e).attr('charset');
                htmlHeadContent = $(e).attr('content');

                if (typeof(htmlHeadCharset) != 'undefined') {

                    charset = htmlHeadCharset;
                }

                if (typeof(htmlHeadContent) != 'undefined') {

                    if (htmlHeadContent.match(/charset=/ig)) {

                        index = htmlHeadContent.indexOf('=');
                        charset = htmlHeadContent.substring(index + 1);
                    }
                }
            });

            if (charset.match(/gb/ig)) {
                decodeHtmlData = iconv.decode(bufferHtmlData, 'gbk');
            }
            else {
                decodeHtmlData = iconv.decode(bufferHtmlData, 'utf8');
            }

            op(decodeHtmlData);
        });

    });

    function op(data) {
        var str=data.replace('_ntes_quote_callback(', '').replace(');', '');
        var json=JSON.parse(str);

        var openRange=0, closeRange=0;
        for(var item in json){
            var e=json[item];
            closeRange=e.yestclose > e.price ? -(1-(e.price/e.yestclose)) : (e.price/e.yestclose)-1;
            openRange=e.yestclose > e.open ? -(1-(e.open/e.yestclose)) : (e.open/e.yestclose)-1;
            res.write(e.name +'   :   '+ toPercent(openRange) +'   :   '+toPercent(closeRange)+'   :   '+e.code+'<br/>');
        }

    }
}

